import Operation from "../Operation.tsx";
import lz4 from "lz4js";

/**
 * LZ4 Compress operation
 */
class LZ4Compress extends Operation {
  /**
   * LZ4Compress constructor
   */
  constructor() {
    super();

    this.name = Dot("lz4.name", "LZ4 Compress");
    this.module = "Compression";
    this.description = Dot(
      "lz4.desc",
      "LZ4 is a lossless data compression algorithm that is focused on compression and decompression speed. It belongs to the LZ77 family of byte-oriented compression schemes."
    );
    this.infoURL = "https://wikipedia.org/wiki/LZ4_(compression_algorithm)";
    this.inputType = "ArrayBuffer";
    this.outputType = "ArrayBuffer";
    this.args = [];
  }

  /**
   * Get detailed information about the operation.
   */
  public getOptDetail(): OptDetail {
    return {
      relatedID: 'nothing', // keep it, don't change
      config: {
        "module": this.module,
        "description": this.description,
        "infoURL": this.infoURL,
        "inputType": this.inputType,
        "outputType": this.outputType,
        "flowControl": false,
        "manualBake": false,
        "args": this.args
      },
      infoURL: this.infoURL,
      nousenouseID: 'lz4compress',
      optName: Dot("lz4.optName", "Perform {0}", this.name),
      optDescription: Dot(
        "lz4.optDesc",
        "This operation compresses data using the {0} algorithm.",
        "LZ4"
      ),
      exampleInput: "<ArrayBuffer of data to compress>", // Placeholder, actual value depends on the input data
      exampleOutput: "<Compressed ArrayBuffer>", // Placeholder, actual value depends on the compression result
    }
  }

  /**
   * @param {ArrayBuffer} input
   * @param {Object[]} args
   * @returns {ArrayBuffer}
   */
  run(input, args) {
    const inBuf = new Uint8Array(input);
    const compressed = lz4.compress(inBuf);
    return compressed.buffer;
  }
}

export default LZ4Compress;
```

